import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import './App.css';

// Import Chart.js components (ensure you register them according to your version)
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [activeTab, setActiveTab] = useState("text");

  // For Direct Text Analysis
  const [text, setText] = useState("");
  const [textResult, setTextResult] = useState(null);

  // For News Analysis (daily)
  const [ticker, setTicker] = useState("");
  const [date, setDate] = useState("");
  const [newsResult, setNewsResult] = useState(null);

  // For Last Week Sentiment Data
  const [lastWeekData, setLastWeekData] = useState(null);

  // Update API URL as needed
  const API_URL = 'http://localhost:3000' //process.env.REACT_APP_API_URL //|| "https://onsent.xyz";

  // Analyze direct text sentiment
  const handleTextSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/analyze-text`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });
      const data = await response.json();
      setTextResult(data);
    } catch (error) {
      console.error("Error analyzing text:", error);
    }
  };

  // Analyze news sentiment for a specific day (using the date input)
  const handleNewsSubmit = async (e) => {
    e.preventDefault();
    // Clear previous last week results if any
    setLastWeekData(null);
    try {
      const response = await fetch(`${API_URL}/analyze-news?ticker=${ticker}&date=${date}`);
      const data = await response.json();
      setNewsResult(data);
    } catch (error) {
      console.error("Error analyzing news:", error);
    }
  };

  // Retrieve last week's sentiment (graph the weekâ€™s results)
  const handleLastWeekSubmit = async () => {
    // Clear previous daily analysis result if any
    setNewsResult(null);
    try {
      const response = await fetch(`${API_URL}/analyze-last-week?ticker=${ticker || "nvidia"}`);
      const data = await response.json();
      setLastWeekData(data.results);
    } catch (error) {
      console.error("Error retrieving last week's sentiment:", error);
    }
  };

  // Prepare data for chart if lastWeekData is available
  const chartData = lastWeekData ? {
    labels: Object.keys(lastWeekData), // dates as labels
    datasets: [
      {
        label: 'Total Sentiment Score',
        data: Object.values(lastWeekData).map(day => day.total_score),
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  } : null;

  return (
    <div className="container">
      <h1>Sentiment Analyzer</h1>

      {/* Tabs Navigation */}
      <div className="tabs">
        <button 
          className={activeTab === "text" ? "active" : ""} 
          onClick={() => setActiveTab("text")}
        >
          Text
        </button>
        <button 
          className={activeTab === "news" ? "active" : ""} 
          onClick={() => setActiveTab("news")}
        >
          News
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === "text" && (
        <section className="section">
          <h2>Analyze Text</h2>
          <form onSubmit={handleTextSubmit}>
            <textarea 
              placeholder="Enter text to get sentiment..."
              value={text}
              onChange={(e) => setText(e.target.value)}
              rows="5"
              style={{ width: "100%" }}
            />
            <br />
            <button type="submit">Analyze Text</button>
          </form>
          {textResult && (
            <div className="result">
              <h3>Analysis Result:</h3>
              <p><strong>Sentiment:</strong> {textResult.label}</p>
              <p><strong>Score:</strong> {textResult.score}</p>
            </div>
          )}
        </section>
      )}

      {activeTab === "news" && (
        <section className="section">
          <h2>News Sentiment Analysis</h2>
          {/* Common Inputs for ticker and date */}
          <form onSubmit={handleNewsSubmit}>
            <input 
              type="text" 
              placeholder="Enter ticker or keywords" 
              value={ticker}
              onChange={(e) => setTicker(e.target.value)}
              required
            />
            <br /><br />
            <input 
              type="date" 
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
            <br /><br />
            <button type="submit">Analyze News (Day)</button>
          </form>
          <br />
          {/* Button for last week sentiment analysis */}
          <button onClick={handleLastWeekSubmit}>Analyze News (Last Week)</button>

          {/* Display results for daily analysis */}
          {newsResult && (
            <div className="result">
              <h3>Overall Sentiment: {newsResult.overall_sentiment}</h3>
              <p><strong>Total Score:</strong> {newsResult.total_score}</p>
              <h4>Articles:</h4>
              <ul>
                {newsResult.articles && newsResult.articles.map((article, index) => (
                  <li key={index}>
                    <a href={article.url} target="_blank" rel="noopener noreferrer">
                      {article.title}
                    </a>
                    <br />
                    <span>
                      Sentiment: {article.sentiment} 
                      {article.score ? `(Score: ${article.score})` : ""}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Display graph results for last week's data */}
          {lastWeekData && (
            <div className="result">
              <h3>Last Week's Sentiment (Business Days)</h3>
              {chartData && <Line data={chartData} />}
              <ul>
                {Object.entries(lastWeekData).map(([date, result]) => (
                  <li key={date}>
                    <strong>{date}:</strong> {result.overall_sentiment} (Score: {result.total_score})
                  </li>
                ))}
              </ul>
            </div>
          )}
        </section>
      )}
    </div>
  );
}

export default App;
