import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [sentiment, setSentiment] = useState(null);
  const [stock, setStock] = useState(null);
  const [optionsVolume, setOptionsVolume] = useState(null);

  useEffect(() => {
    // Simulate backend response
    const data = {
      sentiment: {
        "2025-04-01": { total_score: 0.42 },
        "2025-04-02": { total_score: -0.12 },
        "2025-04-03": { total_score: 0.33 },
      },
      stock: {
        "2025-04-01": 890.5,
        "2025-04-02": 875.3,
        "2025-04-03": 910.2,
      },
      options_volume: {
        "2025-04-01": { calls: 5000, puts: 2000 },
        "2025-04-02": { calls: 3000, puts: 3500 },
        "2025-04-03": { calls: 6000, puts: 2500 },
      }
    };

    setSentiment(data.sentiment);
    setStock(data.stock);
    setOptionsVolume(data.options_volume);
  }, []);

  if (!sentiment || !stock || !optionsVolume) return <div>Loading data...</div>;

  const dates = Object.keys(sentiment).sort();

  const lineData = {
    labels: dates,
    datasets: [
      {
        label: 'Sentiment Score',
        data: dates.map(d => Number(sentiment[d]?.total_score || 0).toFixed(3)),
        borderColor: 'blue',
        tension: 0.3,
        yAxisID: 'y',
      },
      {
        label: 'Stock Price',
        data: dates.map(d => stock[d]),
        borderColor: 'red',
        tension: 0.3,
        yAxisID: 'y1',
      }
    ]
  };

  const lineOptions = {
    responsive: true,
    scales: {
      y: {
        position: 'left',
        title: { display: true, text: 'Sentiment' },
      },
      y1: {
        position: 'right',
        title: { display: true, text: 'Stock Price' },
        grid: { drawOnChartArea: false },
      }
    }
  };

  const barData = {
    labels: dates,
    datasets: [
      {
        label: 'Calls Volume',
        backgroundColor: 'green',
        data: dates.map(d => optionsVolume[d]?.calls || 0),
      },
      {
        label: 'Puts Volume',
        backgroundColor: 'orange',
        data: dates.map(d => optionsVolume[d]?.puts || 0),
      }
    ]
  };

  const barOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Options Volume' },
    },
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Stock & Sentiment Analysis</h2>
      <Line data={lineData} options={lineOptions} />
      <div style={{ height: 50 }} />
      <Bar data={barData} options={barOptions} />
    </div>
  );
}

export default App;
